INSERT INTO `data_learning_materials` VALUES (8,2,2,1,'Nature Interpretation Programmes in Minneriya, Kaudulla & Wilpattu National Parks','natureinterpretationprogrammesinminneriya,kaudulla&wilpattunationalparks6396dde808fb0',2,'{\n   \"root\":{\n      \"children\":[\n         {\n            \"children\":[\n               {\n                  \"detail\":0,\n                  \"format\":0,\n                  \"mode\":\"normal\",\n                  \"style\":\"\",\n                  \"text\":\"On the path to becoming a globally recognized destination for a total wildlife experience, The Federation of Environmental Organizations (FEO) trained over 1,200 Safari Jeep Drivers. This was a structural approach to create more responsibility in the Sri Lankan Wildlife Tourism Industry through an easy approach; the ‘Nature Interpretation’ Training Programme. The participants gained valuable knowledge on the flora and fauna found within each respective National Park, a brief history of the Park, including places of archaeological significance located within them, a reinforcement of the Park Rules and Regulations, and examples of poor visitor reviews and their negative impact on the industry. In addition, they were educated on tourism trends and visitor expectations in detail, the behaviour of the iconic species of Sri Lanka - the elephant, leopard and bear – as observed by them on every sighting, to share this knowledge with their customers and further enhance their ‘Total Wildlife Experience’. An additional 40 Jeep Drivers were trained from the Minneriya and Kaudulla area in August 2022.\",\n                  \"type\":\"text\",\n                  \"version\":1\n               }\n            ],\n            \"direction\":\"ltr\",\n            \"format\":\"\",\n            \"indent\":0,\n            \"type\":\"paragraph\",\n            \"version\":1\n         }\n      ],\n      \"direction\":\"ltr\",\n      \"format\":\"\",\n      \"indent\":0,\n      \"type\":\"root\",\n      \"version\":1\n   }\n}\n','[]','[]','2018-06-01','Federation of Environmental Organizations',NULL,NULL,1,1,'2022-12-12 07:53:12','2022-12-12 07:53:12',NULL,'On the path to becoming a globally recognized destination for a total wildlife experience, The Federation of Environmental Organizations (FEO) trained over 1,200 Safari Jeep Drivers.','[]','[]'),(10,2,2,24,'Introduction to Project Management','introductiontoprojectmanagement63976155b717c',2,'{\n   \"root\":{\n      \"children\":[\n         {\n            \"children\":[\n               {\n                  \"detail\":0,\n                  \"format\":0,\n                  \"mode\":\"normal\",\n                  \"style\":\"\",\n                  \"text\":\"This presentation was a part of FEO’s capacity building programmes conducted for CSOs around the country. This lecture of project management was presented by FEO Trustee, Lalith Dassenaike. Presentation Video Link: https://www.youtube.com/watch?v=9ko6kMbAujM&t=987s\",\n                  \"type\":\"text\",\n                  \"version\":1\n               }\n            ],\n            \"direction\":\"ltr\",\n            \"format\":\"\",\n            \"indent\":0,\n            \"type\":\"paragraph\",\n            \"version\":1\n         }\n      ],\n      \"direction\":\"ltr\",\n      \"format\":\"\",\n      \"indent\":0,\n      \"type\":\"root\",\n      \"version\":1\n   }\n}','[]','[]','2022-06-01','Federation of Environmental Organizations',NULL,NULL,1,1,'2022-12-12 17:13:57','2022-12-12 17:13:57',NULL,'This presentation was a part of FEO’s capacity building programmes conducted for CSOs around the country.','[]','[]'),(11,2,2,15,'Conscious Consumerism; a presentation by Vraîe Cally Balthazaar','consciousconsumerism;apresentationbyvraîecallybalthazaar63976208d6f03',2,'{\n   \"root\":{\n      \"children\":[\n         {\n            \"children\":[\n               {\n                  \"detail\":0,\n                  \"format\":0,\n                  \"mode\":\"norma